mapper.update:
update hm_host

    type=#{host.type},
    money=#{host.money},
    status=#{host.status}

where create_by=#{host.createBy}

mapper.delete:
delete from hm_user_role
where userId=#{userId}
and roleId=#{roleId}

mapper.select:
select user_id
from hm_user_role
where user_id=#{userId}
and role_id=#{roleId}
